svg.script:
var style;
function make_shape(evt){
  var SVGDoc = evt.getTarget().getOwnerDocument();
  var SVGRoot = SVGDoc.getDocumentElement();

  var rect;
  rect = SVGDoc.createElement("rect");
  rect.setAttribute("x", 40);
  rect.setAttribute("y", 20);
  rect.setAttribute("width", 200);
  rect.setAttribute("height", 200);
  style = rect.getStyle();
  style.setProperty("fill","blue");
  rect.addEventListner("click", color_change, false);
  SVGRoot.appendChild(rect);
}
function color_change(){
  var fill = style.getPropertyValue("fill");
  if(fill == "red") style.setProperty("fill", "blue");
  else style.setProperty("fill", "red");
}
